function formatPrice(t){return t.toString().replace(/\B(?=(\d{3})+(?!\d))/g,",")}$(document).ready((function(){$("#salesTable table tbody").empty(),$.ajax({url:"https://iptv.givethanksgrocers.com/karamoto/sales_api.php",type:"GET",dataType:"json",success:function(t){t.forEach((function(t,e){var a=formatPrice(t.SalePrice),n=`\n                    <tr data-id="${t.SaleID}">\n                        <td>${e+1}</td>\n                        <td>${t.VehicleMMY}</td>\n                        <td>${t.VehicleVIN}</td>\n                        <td>${t.BuyerName}</td>\n                        <td>${t.SaleDate}</td>\n                        <td>${a}</td>\n                        <td>${t.PaymentMethod}</td>\n                        <td>${t.Status}</td>\n                    </tr>`;$("#salesTable table tbody").append(n)}))},error:function(t,e,a){console.error("Error fetching sales data:",a)}})}));